mapper.update:

	UPDATE NOTICE
	SET deleted = '1', TITLE = #{title}
	WHERE N_NO = #{nNo}


	UPDATE NOTICE
	SET TITLE = #{title}, CONTENT = #{content}
	WHERE N_NO = #{nNo}

mapper.select:
	
		SELECT n_no nNo, title title, content content, m_id mId, reg_date regDate

		FROM NOTICE
		
		WHERE n_no = #{nNo}
