mapper.update:

	UPDATE QNA
	SET GROUP_NO = #{qNo}
	WHERE Q_NO = #{qNo}


	UPDATE QNA
	SET deleted = '1', TITLE = #{title}
	WHERE Q_NO = #{qNo}


	UPDATE QNA
	SET TITLE = #{title}, CONTENT = #{content}
	WHERE Q_NO = #{qNo}

mapper.select:

	SELECT q_no qNo, title title, content content, m_id mId, reg_date regDate, GROUP_NO groupNo, STEP step, DEPTH depth
	FROM QNA
	WHERE q_no = #{qNo}
