mapper.insert:
insert into EVALUATION_COMMENT (
	M_ID
	, CARD_NO
	, CONTENT
	)
values (
	#{mId}
	, #{cardNo}
	, #{content}
	)

	insert EVALUATION_RATING_LIKED (
		E_RATING_NO
		, LIKED_M_ID
		)
	select
		#{eRatingNo}, #{likedmId}
	from dual
	where #{mId} != #{likedmId}
		AND exists (
			select
				E_RATING_NO
	        from EVALUATION_RATING
	        where E_RATING_NO = #{eRatingNo}
	        	AND deleted = 0
        	)
        AND not exists (
			select
				ER_LIKED
			from EVALUATION_RATING_LIKED
			where E_RATING_NO = #{eRatingNo}
				AND LIKED_M_ID = #{likedmId}
			)

mapper.select:
select exists (
	select E_RATING_NO
	from EVALUATION_RATING
	where CARD_NO = #{cardNo}
		AND M_ID = #{mId}
		AND deleted = 0
	limit 1
	)

select exists (
	select E_COMMENT_NO
	from EVALUATION_COMMENT
	where CARD_NO = #{cardNo}
		AND M_ID = #{mId}
		AND deleted = 0
	limit 1
	)

mapper.update:
update EVALUATION_RATING
set deleted = 1
where E_RATING_NO = #{eRatingNo}

update EVALUATION_COMMENT
set deleted = 1
where E_COMMENT_NO = #{eCommentNo}
